func:
msg.query = msg.payload.q;
msg.followup = msg.payload.f;
msg.history = msg.payload.h;

msg.url = `https://duckduckgo.com/html/?q=${msg.query}`;

return msg;

msg.results = msg.payload.map(item => {
    return {
        title: item.title.trim(),
        displayLink: item.displayLink.trim(),
        htmlSnippet: item.htmlSnippet.trim(),
        link: item.link.trim()
    }
}).slice(0, 6);

msg.payload = msg.payload.map(x => "https://" + x.displayLink.trim()).slice(0, 6);





return msg;

var prompt = `Answer questions "${msg.followup ? msg.followup : msg.query}" using the below text.`;
if (msg.history) {
    prompt += `For context, these are previous questions that were asked: ${msg.history}`;
}
prompt += `Text:\n\n${msg.payload.join("\n")}`
prompt += `\n\nAnswer: `

msg.url = null;

msg.payload = {
    "model": "text-davinci-003",
    "prompt": prompt,
    "temperature": 0,
    "max_tokens": 40,
    "top_p": 1,
    "frequency_penalty": 0,
    "presence_penalty": 0,
    "stop": ["\n"]
}

return msg;

msg.openai = msg.payload;

msg.answer = msg.payload.choices[0].text;

msg.payload = {
    query: msg.query,
    followup: msg.followup,
    answer: msg.answer,
    results: msg.results
};

return msg;
